import {ShopActionTypes} from './shop.types'
import {firestore , convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils'

export const fetchCollectionStart = (collections) =>({
    type:ShopActionTypes.FETCH_COLLECTIONS_START,
})
export const fetchCollectionSuccess = mapCollection=>({
    type :ShopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload : mapCollection
})

export const fetchCollectionStartAsync = () => {
    return dispatch => {
        const collectionRef = firestore.collection("collections");
        dispatch(fetchCollectionStart())
    collectionRef.get().then(async snapshot => {
      const mapCollection = convertCollectionsSnapshotToMap(snapshot);
        dispatch(fetchCollectionSuccess(mapCollection))
    });
    }
}
