import { takeLatest, call, all, put } from "redux-saga/effects";

import {
  googleSignInSucess,
  googleSignInFailure,
  emailSignInFailure,
  emailSignInSuccess
} from "./user.actions";
import UserActionTypes from "./user.types";

import {
  auth,
  googleProvider,
  createUserProfileDocument,
  convertCollectionsSnapshotToMap
} from "../../firebase/firebase.utils";
export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    console.log(userRef);
    const userSnapShot = yield userRef.get();
    yield put(
      googleSignInSucess({ id: userSnapShot.id, ...userSnapShot.data() })
    );
  } catch (error) {
    yield put(googleSignInFailure(error));
  }
}
export function* signInWithEmail({payload: { email, password } }){
  console.log(email, password);
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email ,passwords)
    const userRef = yield call(createUserProfileDocument, user);
    console.log(userRef);
    const userSnapShot = yield userRef.get();
    yield put(
      emailSignInSuccess({ id: userSnapShot.id, ...userSnapShot.data() })
    );
  } catch (error) {
    yield put(emailSignInFailure(error));
  }
}
export function* onGoogleSignStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGNIN_START, signInWithGoogle);
}
export function* onEmailSignInStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGNIN_START, signInWithEmail);
}
export function* UserSaga() {
  yield all([call(onGoogleSignStart), call(onEmailSignInStart)]);
}
